#pragma config(Sensor, port11, color,          sensorVexIQ_ColorGrayscale)
#pragma config(Sensor, port12, gyro,           sensorVexIQ_Gyro)
#pragma config(Motor,  motor1,          leftWheels,    tmotorVexIQ, PIDControl, driveLeft, encoder)
#pragma config(Motor,  motor7,          rightWheels,   tmotorVexIQ, PIDControl, reversed, driveRight, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

void printGyroPosition() {
	displayTextLine(0, "gyro = %d", getGyroDegrees(gyro));
}

void printColorSensor() {
	displayTextLine(0, "color = %d", getColorGrayscale(color));
}

task main()
{

	repeat(forever) {
		int threshold = 10;
		int leftJoystickY;
		int leftJoystickX;
		int leftSpeed;
		int rightSpeed;

		leftJoystickY = getJoystickValue(ChA);
		leftJoystickX = getJoystickValue(ChB);

		leftSpeed = (leftJoystickY + leftJoystickX);
		rightSpeed = (leftJoystickY - leftJoystickX);

		if(leftSpeed > threshold || leftSpeed < -threshold)
		{
			setMotorSpeed(leftWheels, leftSpeed);
		}
		else
		{
			setMotorSpeed(leftWheels, 0);
		}

		if(rightSpeed > threshold || rightSpeed < -threshold)
		{
			setMotorSpeed(rightWheels, rightSpeed);
		}
		else
		{
			setMotorSpeed(rightWheels, 0);
		}

		sleep(75);
		printGyroPosition();
		printColorSensor();
	}


}
